-- headless
local h = owner.Character.Humanoid h.RequiresNeck = false h.Parent.Torso.Neck:Destroy()  local t = Instance.new("Tool",h.Parent) t.Name = owner.Name.."'s Head" local ha = Instance.new("Part") ha.Name = "Handle" ha.Parent = t local weld = Instance.new("Weld") ha.Size = Vector3.new(1,1,0) weld.Parent = ha weld.Part0 = ha weld.Part1 = h.Parent.Head ha.Transparency = 1 t.Unequipped:Connect(function() eq = false h.Parent.Head.Anchored = true h.Parent.Head.CFrame = h.Parent.Torso.CFrame h.Parent.Head.CanCollide = false h.Parent.Head.Transparency = 1 h.Parent.Hat1.Handle.Transparency = 1 h.Parent.Head.face.Transparency = 1 while not eq do wait() h.Parent.Head.Position = h.Parent.Torso.Position + Vector3.new(0,2,0) end end) t.Equipped:Connect(function() eq = true h.Parent.Head.Position = ha.Position h.Parent.Head.CanCollide = true h.Parent.Head.Anchored = false h.Parent.Head.Transparency = 0 h.Parent.Head.face.Transparency = 0 h.Parent.Hat1.Handle.Transparency = 0 h.Parent.Head.face.Transparency = 0 end)
